//React
import React from 'react';
//third-party
import {connect} from 'react-redux';
//components
import Layout from '../components/Layout';
import {fetchHostels} from '../store/hostels';

const  Default = (props) => {
        return (
            <Layout>
                <p>The real content of redux </p>
                {
                    props.loading ? (
                        <p>Loading</p>
                    ) : (
                        props.hostels.map((hostel,i) => (
                            <img src={hostel['image']} alt="" key={i}/>
                        ))
                    )
                }
            </Layout>
        )

};
Default.getInitialProps = ({store,isServer,pathname,query}) => {
    return store.dispatch(fetchHostels());
}
const mapToProps = state => ({
    loading:state.hostels.loading,
    hostels:state.hostels.hostels
})
export default connect(mapToProps,null)(Default);
