import React from 'react';
//third-party
import {connect} from 'react-redux';
//components
import Layout from '../components/Layout';
import {fetchHostels} from '../store/hostels';

class Default extends React.Component{
    async getInitialProps({store,isServer,pathname,query}){
        store.dispatch(await fetchHostels())
        //dispatch to  store from here
        console.log(`isServer`,isServer);
        console.log(`pathname`,pathname);
        console.log(`query`,query)
        console.log(`store`,store.getState())
        // store.dispatch({type:"FOO",payload:"kenny"});
        // return {custom:"custom"};

    };

    render(){
        return (
            <Layout>
                <p>The real content of redux </p>
            </Layout>
        )
    }
}
export default connect()(Default);
