//React
import React from 'react';
//third-party
import {connect} from 'react-redux';
//components
import Layout from '../components/Layout';
import {fetchHostels} from '../store/hostels';

class Default extends React.Component{
    getInitialProps({store,isServer,pathname,query}){
        store.dispatch(fetchHostels());
    };

    render(){
        return (
            <Layout>
                <p>The real content of redux </p>
                {
                    this.props.loading ? (
                        <p>Loading</p>
                    ) : (
                        this.props.hostels.map((hostel,i) => (
                            <img src={hostel['image']} alt="" key={i}/>
                        ))
                    )
                }
            </Layout>
        )
    }
};
const mapToProps = state => ({
    loading:state.hostels.loading,
    hostels:state.hostels.hostels
})
export default connect(mapToProps,null)(Default);
