import React,{useState} from 'react';
//core-components
import Link from 'next/link'
//third-party
import {IoMdShare} from 'react-icons/io';
import {Card,CardHeader,CardMedia,CardContent,Avatar,makeStyles,CardActions} from '@material-ui/core';
import {red} from '@material-ui/core/colors';
//components
import ModalShare from './ModalShare';

const useStyles = makeStyles(theme => ({
    root:{
        background:'rgb(19,38,57)',
        color:'#fff',
        marginTop:'2px',
        marginBottom:'5px'
    },
    media:{
        height:0,
        paddingTop:'56.25%'
    },
    avatar:{
        backgroundColor:red[500]
    }
}));

const HostelCard = ({data}) => {

    const classes = useStyles();
    const [isOpen,setIsOpen] = useState(false);
    const toggle = () => setIsOpen(!isOpen);
    let title = `${data['name']} hostel in allcomrades platform`;
    let message  = `share ${data['name']} hostel via other platforms`;
    let link = `https://allcomrades.co.ke/platform/hostels/${data['_id']}`;

    return (
        <Card className={classes.root}>
           <CardHeader 
             avatar={
                 <Avatar aria-label="hostel" className={classes.avatar}>
                  {
                  data.name[0].toUpperCase()
                  }
                 </Avatar>
             }
             title={
                 <Link className="hostel_card-name" href={`/hostels/${data['slug']}`}>
                     <a>{data['name']}</a>
                 </Link>
             }
             subheader={
                <h5
                className="hostel_card-subheader">
                    {data.location}
                </h5>
             }
             />
            <CardMedia
            className={classes.media}
            image={data.profiles[0]}
            title={data.name}
            />
            <CardContent>
            <div className="hostel_card-actions">
            <p className="hostel_card-description">{data.type}.</p>

            <IoMdShare className="hostel_card_icon--link" onClick={toggle}/>
            <ModalShare title={title} toggle={toggle} isOpen={isOpen} message={message} link={link} />
            </div>

            </CardContent>
            <CardActions>
                <button className="btn btn-success">
                        view more
                </button>
            </CardActions>
        </Card>
    )
};

export default HostelCard;
