//React
import React from 'react';
//third-party
import {connect} from 'react-redux';
//components
import Layout from '../components/Layout';
import {fetchHostels} from '../store/hostels';

class Default extends React.Component{
    static getInitialProps({store,isServer,pathname,query}){
        store.dispatch(fetchHostels())
        return {hostels:store.getState().hostels.hostels}

    };

    render(){
        return (
            <Layout>
                <p>The real content of redux </p>
                {
                    this.props.hostels ? this.props.hostels.map((hostel,i) => (
                        <img src={hostel['image']} alt="" key={i}/>
                    )) : null
                }
            </Layout>
        )
    }
}
export default connect()(Default);
